import React, {Component} from 'react';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import getWeb3 from './utils/getWeb3';
import $ from 'jquery';



class App extends Component {

  state = {
      account: '0x0',
      contractInstance: null,
      msg: '0x0',
      signature: '0x0',
      web3: null
    }  

  componentDidMount = async () => {
    try{
      $("#loader").show();
      $("#content").hide();
      const web3 = await getWeb3();
      const accounts = await web3.eth.getAccounts();
      const account = accounts[0];
      this.setState({web3: web3, account}, this.getData);      
    }
    catch(error){
      console.log(error)
    }
  }

  getData = async => {
    console.log("getData");
      const {account} = this.state ;   
      console.log("account:"+ account + "loadin:"+ loading);
      if(loading){
        $("#loader").hide();
        $("#content").show();
      }
  }

  signMessage = async => {
    $("#loader").show();
    $("#content").hide();
    const {web3, account, loading} = this.state;
    this.setState({loading: true});
    const message = web3.utils.sha3($("#message").val());
    console.log("message1234"+ message);
    web3.currentProvider.sendAsync({
      method: 'personal_sign',
      params: [
        web3.utils.fromAscii(message),
        account,
      ],
      from: account,
    }, function (err, res) {
      alert(res);
      console.log(err, res);
      this.setState({signature: res, msg: message});
      const msgName = document.get 
      $("#msg").html("message:"+ '' + message);
      $("#signature").html("signature:"+ '' + res);

      $("#loader").hide();
      $("#content").show();
    });   
  }

  render() {
    const {loading} = this.state;
  return (
    <div className="App">
      <header >
      <title>Dapp Token (DAPP)</title>
      </header>
      <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
        <a className="navbar-brand col-sm-3 col-md-2 mr-0" >Ethereum Signatures</a>
        <ul className="navbar-nav px-3">
          <li className="nav-item text-nowrap">
            <small><a className="nav-link" >Your Account: <div id="account" ><span >{this.state.account}</span></div></a></small>
          </li>
        </ul>
      </nav>
      <div className="container-fluid">
        <div className="row">
          <main role="main" className="col-md-9 ml-sm-auto col-lg-10">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap pt-3 pb-2 mb-3 border-bottom" style={{marginTop: '40px'}}>
              <div id="loader">
                <p className="text-center">Loading...</p>
              </div>
              <div id="content" className="col-lg-9" style={{display: 'none'}}>
                <h2>Sign a Message</h2>
                <p>Sign a message from your account with the form below and broadcast it to the blockchain!</p>
                <form  className="" ref= "myForm" role="form" onSubmit={this.signMessage}>
                  <div className="form-group">
                    <input id="message" className="form-control" type="text"></input>
                  </div>
                  <button type="submit" className="btn btn-primary">Sign & Send</button>
                </form>
                <p id="msg"></p>
                <p id="signature">{this.signature}</p>
                <button id="verify" className="btn btn-primary" style={{display: 'none'}}> Verify Signature</button>
                <p id="address"></p>
              </div>
            </div>
          </main>
        </div>
      </div>
    </div>
  );
  }
}  
export default App;
