import React, {Component} from 'react';
import './App.css';
import '../node_modules/bootstrap/dist/css/bootstrap.min.css';
import getWeb3 from './utils/getWeb3.js';
import '../node_modules/bootstrap/dist/js/bootstrap.min/js';
import $ from 'jquery';



class App extends Component {

  state = {
      account: '0x0',
      loading: false,
      contractInstance: null,
      msg: '0x0',
      signature: '0x0',
      web3: null
    }


  componentDidMount = async () => {
    try{
      $("#loader").show();
      $("#content").hide();
      const web3 = await getWeb3();
      const accounts = await web3.eth.getAccount();
      const account = accounts[0];
      const networkId = await web3.eth.net.getId();
      this.setState({web3, account, loading}, getData);
      //const deployedAddress = await web3.eth.
      //const contractInstance = new web3.eth.Contract()
    }
    catch(error){
      console.log(error)
    }
  }

  getData = async => {
      const {account, web3, loading} = this.state ;   
      if(loading){
        return;
      }
      $('#account').html(account);
      $("#loader").hide();
      $("#content").show();


  } 

  render() {
  return (
    <div className="App">
      <header >
      <title>Dapp Token (DAPP)</title>
      </header>
      <nav className="navbar navbar-dark fixed-top bg-dark flex-md-nowrap p-0 shadow">
        <a className="navbar-brand col-sm-3 col-md-2 mr-0" href="#">Ethereum Signatures</a>
        <ul className="navbar-nav px-3">
          <li className="nav-item text-nowrap">
            <small><a className="nav-link" href="#">Your Account: <span id="account"></span></a></small>
          </li>
        </ul>
      </nav>
      <div className="container-fluid">
        <div className="row">
          <main role="main" className="col-md-9 ml-sm-auto col-lg-10">
            <div className="d-flex justify-content-between flex-wrap flex-md-nowrap pt-3 pb-2 mb-3 border-bottom" style={{marginTop: '40px'}}>
              <div id="loader">
                <p className="text-center">Loading...</p>
              </div>
              <div id="content" className="col-lg-9" style={{display: 'none'}}>
                <h2>Sign a Message</h2>
                <p>Sign a message from your account with the form below and broadcast it to the blockchain!</p>
                <form  className="" role="form">
                  <div className="form-group">
                    <input id="message" className="form-control" type="text"></input>
                  </div>
                  <button type="submit" className="btn btn-primary">Sign & Send</button>
                </form>
                <p id="msg"></p>
                <p id="signature"></p>
                <button id="verify" className="btn btn-primary" style={{display: 'none'}}> Verify Signature</button>
                <p id="address"></p>
              </div>
            </div>
          </main>
        </div>
      </div>
    </div>
  );
  }
}  
export default App;
